import h from 'virtual-dom/h';

function projectLink() {
  return h('a', {
    href: 'https://github.com/esnext-coverage',
    className: 'footer-project-link'
  }, ['esnext-coverage']);
}

export default function footer({timestamp}) {
  return h('div', {
    className: 'footer'
  }, [
    'Generated by ',
    projectLink(),
    ` on ${new Date(timestamp)}`
  ]);
}
